        "delete_phrase_matcher",
        "update_phrase_matcher",
        "calculate_stats",
        "get_settings",
        "update_settings",
        "create_analysis_rule",
        "get_analysis_rule",
        "list_analysis_rules",
        "update_analysis_rule",
        "delete_analysis_rule",
        "get_encryption_spec",
        "initialize_encryption_spec",
        "create_view",
        "get_view",
        "list_views",
        "update_view",
        "delete_view",
        "query_metrics",
        "create_qa_question",
        "get_qa_question",
        "update_qa_question",
        "delete_qa_question",
        "list_qa_questions",
        "create_qa_scorecard",
        "get_qa_scorecard",
