        response = (
            engine_version.ListEngineVersionsResponse(
                engine_versions=[
                    engine_version.EngineVersion(),
                    engine_version.EngineVersion(),
                    engine_version.EngineVersion(),
                ],
                next_page_token="abc",
            ),
            engine_version.ListEngineVersionsResponse(
                engine_versions=[],
                next_page_token="def",
            ),
            engine_version.ListEngineVersionsResponse(
                engine_versions=[
                    engine_version.EngineVersion(),
                ],
                next_page_token="ghi",
            ),
            engine_version.ListEngineVersionsResponse(
                engine_versions=[
                    engine_version.EngineVersion(),
                    engine_version.EngineVersion(),
                ],
            ),
        )
        # Two responses for two calls
        response = response + response

        # Wrap the values into proper Response objs
        response = tuple(
            engine_version.ListEngineVersionsResponse.to_json(x) for x in response
        )
        return_values = tuple(Response() for i in response)
        for return_val, response_val in zip(return_values, response):
            return_val._content = response_val.encode("UTF-8")
            return_val.status_code = 200
        req.side_effect = return_values

        sample_request = {
            "parent": "projects/sample1/locations/sample2/instances/sample3"
        }

        pager = client.list_engine_versions(request=sample_request)

        results = list(pager)
        assert len(results) == 6
        assert all(isinstance(i, engine_version.EngineVersion) for i in results)

        pages = list(client.list_engine_versions(request=sample_request).pages)
        for page_, token in zip(pages, ["abc", "def", "ghi", ""]):
            assert page_.raw_page.next_page_token == token


