def test_aml_base_transport():
    # Instantiate the base transport.
    with mock.patch(
        "google.cloud.financialservices_v1.services.aml.transports.AMLTransport.__init__"
    ) as Transport:
        Transport.return_value = None
        transport = transports.AMLTransport(
            credentials=ga_credentials.AnonymousCredentials(),
        )

    # Every method on the transport should just blindly
    # raise NotImplementedError.
    methods = (
        "list_instances",
        "get_instance",
        "create_instance",
        "update_instance",
        "delete_instance",
        "import_registered_parties",
        "export_registered_parties",
        "list_datasets",
        "get_dataset",
        "create_dataset",
        "update_dataset",
        "delete_dataset",
        "list_models",
        "get_model",
        "create_model",
        "update_model",
        "export_model_metadata",
        "delete_model",
        "list_engine_configs",
        "get_engine_config",
        "create_engine_config",
        "update_engine_config",
        "export_engine_config_metadata",
        "delete_engine_config",
        "get_engine_version",
        "list_engine_versions",
        "list_prediction_results",
        "get_prediction_result",
        "create_prediction_result",
        "update_prediction_result",
        "export_prediction_result_metadata",
        "delete_prediction_result",
        "list_backtest_results",
        "get_backtest_result",
        "create_backtest_result",
        "update_backtest_result",
        "export_backtest_result_metadata",
        "delete_backtest_result",
        "get_location",
        "list_locations",
        "get_operation",
        "cancel_operation",
        "delete_operation",
        "list_operations",
    )
    for method in methods:
        with pytest.raises(NotImplementedError):
            getattr(transport, method)(request=object())

    with pytest.raises(NotImplementedError):
        transport.close()

    # Additionally, the LRO client (a property) should
    # also raise NotImplementedError
    with pytest.raises(NotImplementedError):
        transport.operations_client

    # Catch all for all remaining methods and properties
    remainder = [
        "kind",
    ]
    for r in remainder:
        with pytest.raises(NotImplementedError):
            getattr(transport, r)()


