def test_gdc_hardware_management_base_transport():
    # Instantiate the base transport.
    with mock.patch(
        "google.cloud.gdchardwaremanagement_v1alpha.services.gdc_hardware_management.transports.GDCHardwareManagementTransport.__init__"
    ) as Transport:
        Transport.return_value = None
        transport = transports.GDCHardwareManagementTransport(
            credentials=ga_credentials.AnonymousCredentials(),
        )

    # Every method on the transport should just blindly
    # raise NotImplementedError.
    methods = (
        "list_orders",
        "get_order",
        "create_order",
        "update_order",
        "delete_order",
        "submit_order",
        "list_sites",
        "get_site",
        "create_site",
        "update_site",
        "list_hardware_groups",
        "get_hardware_group",
        "create_hardware_group",
        "update_hardware_group",
        "delete_hardware_group",
        "list_hardware",
        "get_hardware",
        "create_hardware",
        "update_hardware",
        "delete_hardware",
        "list_comments",
        "get_comment",
        "create_comment",
        "list_change_log_entries",
        "get_change_log_entry",
        "list_skus",
        "get_sku",
        "list_zones",
        "get_zone",
        "create_zone",
        "update_zone",
        "delete_zone",
        "signal_zone_state",
        "get_location",
        "list_locations",
        "get_operation",
        "cancel_operation",
        "delete_operation",
        "list_operations",
    )
    for method in methods:
        with pytest.raises(NotImplementedError):
            getattr(transport, method)(request=object())

    with pytest.raises(NotImplementedError):
        transport.close()

    # Additionally, the LRO client (a property) should
    # also raise NotImplementedError
    with pytest.raises(NotImplementedError):
        transport.operations_client

    # Catch all for all remaining methods and properties
    remainder = [
        "kind",
    ]
    for r in remainder:
        with pytest.raises(NotImplementedError):
            getattr(transport, r)()


