def test_list_unit_kinds_rest_pager(transport: str = "rest"):
    client = SaasDeploymentsClient(
        credentials=ga_credentials.AnonymousCredentials(),
        transport=transport,
    )

    # Mock the http request call within the method and fake a response.
    with mock.patch.object(Session, "request") as req:
        # TODO(kbandes): remove this mock unless there's a good reason for it.
        # with mock.patch.object(path_template, 'transcode') as transcode:
        # Set the response as a series of pages
        response = (
            deployments_service.ListUnitKindsResponse(
                unit_kinds=[
                    deployments_resources.UnitKind(),
                    deployments_resources.UnitKind(),
                    deployments_resources.UnitKind(),
                ],
                next_page_token="abc",
            ),
            deployments_service.ListUnitKindsResponse(
                unit_kinds=[],
                next_page_token="def",
            ),
            deployments_service.ListUnitKindsResponse(
                unit_kinds=[
                    deployments_resources.UnitKind(),
                ],
                next_page_token="ghi",
            ),
            deployments_service.ListUnitKindsResponse(
                unit_kinds=[
                    deployments_resources.UnitKind(),
                    deployments_resources.UnitKind(),
                ],
            ),
        )
        # Two responses for two calls
        response = response + response

        # Wrap the values into proper Response objs
        response = tuple(
            deployments_service.ListUnitKindsResponse.to_json(x) for x in response
        )
        return_values = tuple(Response() for i in response)
        for return_val, response_val in zip(return_values, response):
            return_val._content = response_val.encode("UTF-8")
            return_val.status_code = 200
        req.side_effect = return_values

        sample_request = {"parent": "projects/sample1/locations/sample2"}

        pager = client.list_unit_kinds(request=sample_request)

        results = list(pager)
        assert len(results) == 6
        assert all(isinstance(i, deployments_resources.UnitKind) for i in results)

        pages = list(client.list_unit_kinds(request=sample_request).pages)
        for page_, token in zip(pages, ["abc", "def", "ghi", ""]):
            assert page_.raw_page.next_page_token == token


