def test_saas_deployments_base_transport():
    # Instantiate the base transport.
    with mock.patch(
        "google.cloud.saasplatform_saasservicemgmt_v1beta1.services.saas_deployments.transports.SaasDeploymentsTransport.__init__"
    ) as Transport:
        Transport.return_value = None
        transport = transports.SaasDeploymentsTransport(
            credentials=ga_credentials.AnonymousCredentials(),
        )

    # Every method on the transport should just blindly
    # raise NotImplementedError.
    methods = (
        "list_saas",
        "get_saas",
        "create_saas",
        "update_saas",
        "delete_saas",
        "list_tenants",
        "get_tenant",
        "create_tenant",
        "update_tenant",
        "delete_tenant",
        "list_unit_kinds",
        "get_unit_kind",
        "create_unit_kind",
        "update_unit_kind",
        "delete_unit_kind",
        "list_units",
        "get_unit",
        "create_unit",
        "update_unit",
        "delete_unit",
        "list_unit_operations",
        "get_unit_operation",
        "create_unit_operation",
        "update_unit_operation",
        "delete_unit_operation",
        "list_releases",
        "get_release",
        "create_release",
        "update_release",
        "delete_release",
        "get_location",
        "list_locations",
    )
    for method in methods:
        with pytest.raises(NotImplementedError):
            getattr(transport, method)(request=object())

    with pytest.raises(NotImplementedError):
        transport.close()

    # Catch all for all remaining methods and properties
    remainder = [
        "kind",
    ]
    for r in remainder:
        with pytest.raises(NotImplementedError):
            getattr(transport, r)()


